select customer.first_name as Nombre,customer.last_name as Apellido,country.country as Pais,city.city as Ciudad,address.district as Distrito,address.address as Direccion,address.phone as Telefono from customer left join address on customer.address_id=address.address_id left join city on address.city_id=city.city_id left join country on city.country_id=country.country_id where country.country like 'Japan';
